            }}
          />
          <input
            type="text"
            placeholder="age"
            name="age"
            onChange={(e) => {
              setAge(e.target.value);
            }}
          />
          <button style={{ backgroundColor: "green" }}>
            submit and add animal
          </button>
        </form>
      </form>
    </div>
  );
};

export default AddAnimal;
